import React, { useState, useRef, useEffect } from 'react';

import { MessageCircle, X, Send, Bot, User } from 'lucide-react';

import { getApiUrl } from './config';

import './App.css';
 
interface Message {

  id: string;

  text: string;

  sender: 'user' | 'bot';

  timestamp: Date;

  isStreaming?: boolean;

}
 
function App() {

  const [isChatOpen, setIsChatOpen] = useState(false);

  const [messages, setMessages] = useState<Message[]>([

    {

      id: '1',

      text: "Hello! I'm your AI Tutor. I'm here to help you learn and answer any questions you might have. What would you like to learn today?",

      sender: 'bot',

      timestamp: new Date()

    }

  ]);

  const [inputText, setInputText] = useState('');

  const [isLoading, setIsLoading] = useState(false);
 
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const inputRef = useRef<HTMLInputElement>(null);

  const controllerRef = useRef<AbortController | null>(null); // ðŸš¨ Added controller
 
  const scrollToBottom = () => {

    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });

  };
 
  useEffect(() => {

    scrollToBottom();

  }, [messages]);
 
  useEffect(() => {

    if (isChatOpen && inputRef.current) {

      inputRef.current.focus();

    }

  }, [isChatOpen]);
 
  const handleSendMessage = async () => {

    if (!inputText.trim() || isLoading) return;
 
    const userMessage: Message = {

      id: Date.now().toString(),

      text: inputText,

      sender: 'user',

      timestamp: new Date()

    };
 
    setMessages(prev => [...prev, userMessage]);

    setInputText('');

    setIsLoading(true);
 
    const botMessageId = (Date.now() + 1).toString();

    const streamingMessage: Message = {

      id: botMessageId,

      text: '',

      sender: 'bot',

      timestamp: new Date(),

      isStreaming: true

    };
 
    setMessages(prev => [...prev, streamingMessage]);
 
    try {

      const apiUrl = getApiUrl('/api/Tutor/ask');

      const requestBody = {

        message: inputText,

        question: inputText,

        query: inputText,

        prompt: inputText

      };
 
      const controller = new AbortController(); // ðŸš¨ Create new controller

      controllerRef.current = controller;
 
      const response = await fetch(apiUrl, {

        method: 'POST',

        headers: {

          'Content-Type': 'application/json',

        },

        body: JSON.stringify(requestBody),

        signal: controller.signal // ðŸš¨ Add signal to fetch

      });
 
      if (!response.ok) {

        const errorText = await response.text();

        throw new Error(`HTTP ${response.status}: ${response.statusText}`);

      }
 
      const contentType = response.headers.get('content-type');
 
      if (contentType && contentType.includes('application/json')) {

        const jsonResponse = await response.json();

        let answer = jsonResponse.answer || jsonResponse.message || jsonResponse.response || jsonResponse.content || JSON.stringify(jsonResponse);
 
        const formatResponse = (text: string) => {

          return text

            .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')

            .replace(/\*(.*?)\*/g, '<em>$1</em>')

            .replace(/```([\s\S]*?)```/g, '<pre><code>$1</code></pre>')

            .replace(/`([^`]+)`/g, '<code>$1</code>')

            .replace(/\n/g, '<br>');

        };
 
        let displayText = '';

        for (let i = 0; i < answer.length; i++) {

          displayText += answer[i];

          const formattedText = formatResponse(displayText);

          setMessages(prev => prev.map(msg =>

            msg.id === botMessageId

              ? { ...msg, text: formattedText }

              : msg

          ));

          await new Promise(resolve => setTimeout(resolve, 20));

        }

      } else {

        const reader = response.body?.getReader();

        if (!reader) throw new Error('No response body');
 
        let accumulatedText = '';
 
        while (true) {

          const { done, value } = await reader.read();

          if (done) break;
 
          const chunk = new TextDecoder().decode(value);

          accumulatedText += chunk;
 
          setMessages(prev => prev.map(msg =>

            msg.id === botMessageId

              ? { ...msg, text: accumulatedText }

              : msg

          ));

        }

      }
 
      setMessages(prev => prev.map(msg =>

        msg.id === botMessageId

          ? { ...msg, isStreaming: false }

          : msg

      ));
 
    } catch (error: any) {

      console.error('Error:', error);

      const errorMessage = error.name === 'AbortError'

        ? 'Generation stopped by user.'

        : error.message || 'Unknown error occurred';
 
      setMessages(prev => prev.map(msg =>

        msg.id === botMessageId

          ? { ...msg, text: `âš ï¸ ${errorMessage}`, isStreaming: false }

          : msg

      ));

    } finally {

      controllerRef.current = null; // ðŸš¨ Clear controller

      setIsLoading(false);

    }

  };
 
  const handleStopGenerating = () => {

    controllerRef.current?.abort();

    controllerRef.current = null;
 
    setMessages(prev =>

      prev.map(msg =>

        msg.isStreaming

          ? { ...msg, isStreaming: false, text: msg.text + ' â›” stopped.' }

          : msg

      )

    );

    setIsLoading(false);

  };
 
  const handleKeyPress = (e: React.KeyboardEvent) => {

    if (e.key === 'Enter' && !e.shiftKey) {

      e.preventDefault();

      handleSendMessage();

    }

  };
 
  return (
<div className="App">
<div className={`chat-widget ${isChatOpen ? 'open' : ''}`}>
<div className="chat-header">
<div className="chat-header-content">
<Bot className="chat-icon" />
<div>
<h3>AI Tutor</h3>
<span className="status">Online</span>
</div>
</div>
<button className="close-button" onClick={() => setIsChatOpen(false)}>
<X size={20} />
</button>
</div>
 
        <div className="chat-messages">

          {messages.map((message) => (
<div key={message.id} className={`message ${message.sender}`}>
<div className="message-avatar">

                {message.sender === 'user' ? <User size={16} /> : <Bot size={16} />}
</div>
<div className="message-content">
<div

                  className="message-text"

                  dangerouslySetInnerHTML={{ __html: message.text }}

                />

                {message.isStreaming && <span className="typing-indicator">â–‹</span>}
<div className="message-time">

                  {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
</div>
</div>
</div>

          ))}
<div ref={messagesEndRef} />
</div>
 
        <div className="chat-input-container">
<input

            ref={inputRef}

            type="text"

            value={inputText}

            onChange={(e) => setInputText(e.target.value)}

            onKeyPress={handleKeyPress}

            placeholder="Type your question..."

            disabled={isLoading}

            className="chat-input"

          />

          {isLoading ? (
<button onClick={handleStopGenerating} className="send-button stop">

              Stop
</button>

          ) : (
<button

              onClick={handleSendMessage}

              disabled={!inputText.trim()}

              className="send-button"
>
<Send size={18} />
</button>

          )}
</div>
</div>
 
      <button

        className="chat-toggle-button"

        onClick={() => setIsChatOpen(!isChatOpen)}
>

        {isChatOpen ? <X size={24} /> : <MessageCircle size={24} />}
</button>
</div>

  );

}
 
export default App;
