import React, { useState, useRef, useEffect } from 'react';
import { MessageCircle, X, Send, Bot, User } from 'lucide-react';
import { getApiUrl } from './config';
import './App.css';

interface Message {
  id: string;
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
  isStreaming?: boolean;
}

function App() {
  const [isChatOpen, setIsChatOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hello! I'm your AI Tutor. I'm here to help you learn and answer any questions you might have. What would you like to learn today?",
      sender: 'bot',
      timestamp: new Date(),
      isStreaming: false // âœ… add this to fix the TypeScript error
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const responseReaderRef = useRef<ReadableStreamDefaultReader<Uint8Array> | null>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (isChatOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isChatOpen]);

  const handleSendMessage = async () => {
    if (!inputText.trim() || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsLoading(true);

    const botMessageId = (Date.now() + 1).toString();
    const streamingMessage: Message = {
      id: botMessageId,
      text: '',
      sender: 'bot',
      timestamp: new Date(),
      isStreaming: true
    };

    setMessages(prev => [...prev, streamingMessage]);

    try {
      const apiUrl = getApiUrl('/api/Tutor/ask');
      const requestBody = {
        message: inputText,
        question: inputText,
        query: inputText,
        prompt: inputText
      };

      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP ${response.status}: ${response.statusText} - ${errorText}`);
      }

      const contentType = response.headers.get('content-type');

      if (contentType && contentType.includes('application/json')) {
        const jsonResponse = await response.json();
        let answer = jsonResponse.answer || jsonResponse.message || jsonResponse.response || jsonResponse.content || (typeof jsonResponse === 'string' ? jsonResponse : JSON.stringify(jsonResponse));

        let displayText = '';
        for (let i = 0; i < answer.length; i++) {
          displayText += answer[i];
          setMessages(prev => prev.map(msg =>
            msg.id === botMessageId
              ? { ...msg, text: displayText }
              : msg
          ));
          await new Promise(resolve => setTimeout(resolve, 20));
        }
      } else {
        const reader = response.body?.getReader();
        if (!reader) throw new Error('No response body');
        responseReaderRef.current = reader;

        let accumulatedText = '';
        const decoder = new TextDecoder();
        let isCancelled = false;

        while (!isCancelled) {
          const { done, value } = await reader.read();
          if (done) break;

          if (!responseReaderRef.current) {
            isCancelled = true;
            break;
          }

          const chunk = decoder.decode(value);
          accumulatedText += chunk;

          setMessages(prev => prev.map(msg =>
            msg.id === botMessageId
              ? { ...msg, text: accumulatedText }
              : msg
          ));
        }
      }

      setMessages(prev => prev.map(msg =>
        msg.id === botMessageId
          ? { ...msg, isStreaming: false }
          : msg
      ));
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
      setMessages(prev => prev.map(msg =>
        msg.id === botMessageId
          ? { ...msg, text: `Error: ${errorMessage}. Please check your backend connection.`, isStreaming: false }
          : msg
      ));
    } finally {
      setIsLoading(false);
      responseReaderRef.current = null;
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleStopClick = () => {
    if (responseReaderRef.current) {
      responseReaderRef.current.cancel('User stopped response');
      responseReaderRef.current = null;
      setIsLoading(false);

      setMessages(prev =>
        prev.map(msg =>
          msg.isStreaming
            ? { ...msg, isStreaming: false, text: msg.text + '\n\n[Response stopped]' }
            : msg
        )
      );
    }
  };

  return (
    <div className="App">
      {/* Chat Widget */}
      <div className={`chat-widget ${isChatOpen ? 'open' : ''}`}>
        {/* Chat Header */}
        <div className="chat-header">
          <div className="chat-header-content">
            <Bot className="chat-icon" />
            <div>
              <h3>AI Tutor</h3>
              <span className="status">Online</span>
            </div>
          </div>
          <button className="close-button" onClick={() => setIsChatOpen(false)}>
            <X size={20} />
          </button>
        </div>

        {/* Chat Messages */}
        <div className="chat-messages">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.sender === 'user' ? 'user' : 'bot'}`}>
              <div className="message-avatar">
                {message.sender === 'user' ? <User size={16} /> : <Bot size={16} />}
              </div>
              <div className="message-content">
                <div className="message-text">
                  {message.text}
                  {message.isStreaming && <span className="typing-indicator">â–‹</span>}
                </div>
                <div className="message-time">
                  {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </div>
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Chat Input */}
        <div className="chat-input-container">
          <input
            ref={inputRef}
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your question..."
            disabled={isLoading}
            className="chat-input"
          />
          <button
            onClick={handleSendMessage}
            disabled={!inputText.trim() || isLoading}
            className="send-button"
          >
            <Send size={18} />
          </button>
          {isLoading && (
            <button onClick={handleStopClick} className="stop-button">
              Stop
            </button>
          )}
        </div>
      </div>

      {/* Floating Chat Button */}
      <button className="chat-toggle-button" onClick={() => setIsChatOpen(!isChatOpen)}>
        {isChatOpen ? <X size={24} /> : <MessageCircle size={24} />}
      </button>
    </div>
  );
}

export default App;
