import React, { useState, useRef, useEffect } from 'react';
import { MessageCircle, X, Send, Bot, User, Square } from 'lucide-react';
// Note: Assuming 'getApiUrl' is a helper function from a separate file
// For this example, we'll mock it for a complete, runnable component.
// In your real application, you would still use your actual getApiUrl.
const getApiUrl = (path: string) => `https://example.com${path}`;

// Interface for a chat message
interface Message {
  id: string;
  text: string;
  sender: 'user' | 'bot';
  timestamp: Date;
  isStreaming?: boolean;
}

// Main App component
export default function App() {
  // State for managing the chat widget's open/closed state
  const [isChatOpen, setIsChatOpen] = useState(false);
  
  // State for storing the list of chat messages
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      text: "Hello! I'm your AI Tutor. I'm here to help you learn and answer any questions you might have. What would you like to learn today?",
      sender: 'bot',
      timestamp: new Date()
    }
  ]);

  // State for the text input field
  const [inputText, setInputText] = useState('');
  
  // State to manage the loading/request in-progress status
  const [isLoading, setIsLoading] = useState(false);
  
  // A ref to store the AbortController, which allows us to cancel the fetch request
  const abortControllerRef = useRef<AbortController | null>(null);

  // Refs for auto-scrolling and input focus
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Effect to scroll to the bottom of the chat messages whenever they update
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Effect to focus the input field when the chat widget opens
  useEffect(() => {
    if (isChatOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isChatOpen]);

  /**
   * Handles sending a new message to the AI.
   * This function initiates a new fetch request and handles streaming responses.
   */
  const handleSendMessage = async () => {
    if (!inputText.trim() || isLoading) return;

    // Create a new AbortController for this specific request
    const abortController = new AbortController();
    abortControllerRef.current = abortController;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputText,
      sender: 'user',
      timestamp: new Date()
    };

    // Add the user's message to the chat
    setMessages(prev => [...prev, userMessage]);
    
    // Clear the input field and set loading state
    setInputText('');
    setIsLoading(true);

    // Create a placeholder bot message for the streaming response
    const botMessageId = (Date.now() + 1).toString();
    const streamingMessage: Message = {
      id: botMessageId,
      text: '',
      sender: 'bot',
      timestamp: new Date(),
      isStreaming: true
    };

    setMessages(prev => [...prev, streamingMessage]);

    try {
      // The API endpoint and request body
      const apiUrl = getApiUrl('/api/Tutor/ask');
      const requestBody = {
        message: userMessage.text,
        question: userMessage.text,
      };

      // Perform the fetch request, passing the AbortController's signal
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
        signal: abortController.signal, // This is the key for cancellation
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      // Check if response is streaming or JSON
      const contentType = response.headers.get('content-type');
      
      if (contentType && contentType.includes('application/json')) {
        const jsonResponse = await response.json();
        const answer = jsonResponse.answer || jsonResponse.message || jsonResponse.response || jsonResponse.content || (typeof jsonResponse === 'string' ? jsonResponse : JSON.stringify(jsonResponse));
        
        // Format the response for better display
        const formatResponse = (text: string) => {
          return text
            .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
            .replace(/\*(.*?)\*/g, '<em>$1</em>')
            .replace(/```([\s\S]*?)```/g, '<pre><code>$1</code></pre>')
            .replace(/`([^`]+)`/g, '<code>$1</code>')
            .replace(/\n/g, '<br>');
        };
        
        // Simulate streaming for JSON responses
        let displayText = '';
        for (let i = 0; i < answer.length; i++) {
          displayText += answer[i];
          const formattedText = formatResponse(displayText);
          setMessages(prev => prev.map(msg => 
            msg.id === botMessageId 
              ? { ...msg, text: formattedText }
              : msg
          ));
          await new Promise(resolve => setTimeout(resolve, 20));
        }
      } else {
        // Handle a true streaming response
        const reader = response.body?.getReader();
        if (!reader) throw new Error('No response body');

        let accumulatedText = '';
        
        while (true) {
          const { done, value } = await reader.read();
          if (done) break;
          
          const chunk = new TextDecoder().decode(value);
          accumulatedText += chunk;
          
          setMessages(prev => prev.map(msg => 
            msg.id === botMessageId 
              ? { ...msg, text: accumulatedText }
              : msg
          ));
        }
      }
    } catch (error) {
      if ((error as any).name === 'AbortError') {
        console.log('Fetch request was aborted.');
        setMessages(prev => prev.map(msg =>
          msg.id === botMessageId ? { ...msg, isStreaming: false, text: msg.text + ' (Request stopped)' } : msg
        ));
      } else {
        console.error('Error:', error);
        const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
        setMessages(prev => prev.map(msg => 
          msg.id === botMessageId 
            ? { ...msg, text: `Error: ${errorMessage}. Please check your backend connection.`, isStreaming: false }
            : msg
        ));
      }
    } finally {
      // Clean up the state regardless of success or failure
      setIsLoading(false);
      setMessages(prev => prev.map(msg => 
        msg.id === botMessageId 
          ? { ...msg, isStreaming: false }
          : msg
      ));
      abortControllerRef.current = null;
    }
  };

  /**
   * Handles stopping the current streaming response.
   * This calls the abort method on the stored AbortController.
   */
  const handleStopGenerating = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
  };

  /**
   * Handles keyboard events, specifically the 'Enter' key for sending messages.
   */
  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  // Helper component for a single chat message
  const ChatMessage = ({ message }: { message: Message }) => {
    const isUser = message.sender === 'user';
    
    // Format the timestamp
    const time = message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

    // CSS class names based on sender
    const containerClasses = `flex items-start gap-3 p-4 rounded-lg shadow-sm ${isUser ? 'justify-end' : ''}`;
    const contentClasses = `flex flex-col gap-1 max-w-[80%] ${isUser ? 'bg-indigo-600 text-white rounded-bl-xl rounded-tr-xl rounded-tl-xl' : 'bg-gray-100 dark:bg-gray-700 text-gray-900 dark:text-gray-100 rounded-br-xl rounded-tr-xl rounded-tl-xl'}`;
    const avatarClasses = `flex-shrink-0 w-8 h-8 flex items-center justify-center rounded-full text-white ${isUser ? 'bg-indigo-500' : 'bg-gray-500 dark:bg-gray-600'}`;

    return (
      <div className={containerClasses}>
        {!isUser && (
          <div className={avatarClasses}>
            <Bot size={16} />
          </div>
        )}
        <div className={contentClasses}>
          <div
            className="text-sm p-3"
            dangerouslySetInnerHTML={{ __html: message.text }}
          />
          {message.isStreaming && <span className="text-sm ml-3 pb-2 typing-indicator">▋</span>}
          <div className={`text-xs text-right p-1 px-3 ${isUser ? 'text-white/70' : 'text-gray-500 dark:text-gray-400'}`}>
            {time}
          </div>
        </div>
        {isUser && (
          <div className={avatarClasses}>
            <User size={16} />
          </div>
        )}
      </div>
    );
  };
  
  return (
    // Main container with a dark background and a sans-serif font
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 font-sans text-gray-100 p-4">
      {/* Tailwind CSS for global styles */}
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;700&display=swap');
        body { font-family: 'Inter', sans-serif; }
        .typing-indicator { animation: pulse 1s infinite ease-in-out; }
        @keyframes pulse {
          0%, 100% { opacity: 0.5; }
          50% { opacity: 1; }
        }
      `}</style>
      
      {/* The main chat widget container */}
      <div className={`fixed bottom-4 right-4 z-50 transition-all duration-300 ${isChatOpen ? 'w-full max-w-md h-[80vh] md:h-[600px] shadow-2xl rounded-xl' : 'w-16 h-16 rounded-full'}`}>
        
        {/* The floating button to open/close the chat */}
        <button 
          className={`absolute bottom-0 right-0 w-16 h-16 rounded-full bg-indigo-600 text-white shadow-lg flex items-center justify-center focus:outline-none focus:ring-4 focus:ring-indigo-300 transition-transform duration-300 transform hover:scale-110 ${isChatOpen ? 'rotate-90 opacity-0 pointer-events-none' : ''}`}
          onClick={() => setIsChatOpen(!isChatOpen)}
          aria-label="Open chat"
        >
          <MessageCircle size={28} />
        </button>
        
        {/* The chat content, only visible when isChatOpen is true */}
        {isChatOpen && (
          <div className="w-full h-full bg-gray-800 rounded-xl flex flex-col overflow-hidden">
            {/* Chat Header */}
            <div className="flex items-center justify-between p-4 border-b border-gray-700 bg-gray-900">
              <div className="flex items-center gap-3">
                <div className="p-2 rounded-full bg-indigo-500 text-white">
                  <Bot size={20} />
                </div>
                <div>
                  <h3 className="text-lg font-semibold">AI Tutor</h3>
                  <span className="text-xs text-green-400">Online</span>
                </div>
              </div>
              <button 
                className="p-2 rounded-full text-gray-400 hover:bg-gray-700 transition-colors"
                onClick={() => setIsChatOpen(false)}
                aria-label="Close chat"
              >
                <X size={20} />
              </button>
            </div>

            {/* Chat Messages */}
            <div className="flex-1 p-4 space-y-4 overflow-y-auto custom-scrollbar">
              {messages.map((message) => (
                <ChatMessage key={message.id} message={message} />
              ))}
              <div ref={messagesEndRef} />
            </div>

            {/* Chat Input */}
            <div className="flex items-center p-4 border-t border-gray-700 bg-gray-900">
              <input
                ref={inputRef}
                type="text"
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your question..."
                disabled={isLoading}
                className="flex-1 px-4 py-2 rounded-full bg-gray-700 text-gray-100 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500 transition-colors"
              />
              {isLoading ? (
                <button
                  onClick={handleStopGenerating}
                  className="ml-2 p-3 rounded-full bg-red-600 text-white hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-300 transition-colors"
                  aria-label="Stop generating response"
                >
                  <Square size={20} />
                </button>
              ) : (
                <button
                  onClick={handleSendMessage}
                  disabled={!inputText.trim()}
                  className="ml-2 p-3 rounded-full bg-indigo-600 text-white hover:bg-indigo-700 disabled:bg-gray-600 disabled:text-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-300 transition-colors"
                  aria-label="Send message"
                >
                  <Send size={20} />
                </button>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
